import React, { useState, useEffect } from "react";
import InfiniteScroll from "react-infinite-scroll-component";
import { api } from "../../api";
import "./CardsAdminPanel.scss";

interface IState {
  stateApi: string;
}
interface ICards {
  name: string;
  categoryName: string;
  tranlation: string;
  img: string;
  sound: string;
}
const CardsAdminPanel: React.FC<IState> = (props) => {
  const [cards, setCards] = useState<ICards[]>([]);
  const [addTrue, setAddTrue] = useState<boolean>(false);
  useEffect(() => {
    fetch(`${api}/cards`)
      .then((res) => res.json())
      .then((res) => {
        if (res.categoryName === props.stateApi) setCards(res);
      });
  }, []);
  function fetchDataLive() {
    fetch(`${api}/cards`)
      .then((res) => res.json())
      .then((res) => {
        if (res.categoryName === props.stateApi) setCards([...cards, ...res]);
      });
  }
  return (
    <div className="container">
      <p className="name_of_ac">
        Category: <span>{props.stateApi}</span>
      </p>
      <InfiniteScroll
        dataLength={cards.length}
        next={fetchDataLive}
        hasMore={true}
        loader={<div className="loader"></div>}
        className="admin__cards"
      >
        <div
          className={addTrue ? "add__words_active" : "add__words"}
          onClick={() => setAddTrue(true)}
        >
          <p className="word__name-h">Add new word</p>
          <svg
            width="150"
            height="150"
            viewBox="0 0 150 150"
            fill="none"
            xmlns="http://www.w3.org/2000/svg"
            xmlnsXlink="http://www.w3.org/1999/xlink"
          >
            <rect
              opacity="0.6"
              x="1.5"
              y="1.5"
              width="147"
              height="147"
              rx="73.5"
              fill="url(#pattern0)"
              stroke="black"
              strokeWidth="3"
            />
            <defs>
              <pattern
                id="pattern0"
                patternContentUnits="objectBoundingBox"
                width="1"
                height="1"
              >
                <use xlinkHref="#image0" transform="scale(0.00195312)" />
              </pattern>
              <image
                id="image0"
                width="512"
                height="512"
                xlinkHref="data:image/png;base64,"
              />
            </defs>
          </svg>
          <form>
            <p className="word__yt">Word:</p>
            <input type="text" placeholder="Word" />
            <p className="word__yt">Translation:</p>
            <input type="text" placeholder="Tranlation" />
            <div className="sound__word">
              <span>Sound</span>
              <div className="select__src">
                <input
                  name="file"
                  type="file"
                  id="input__file"
                  className="input__file"
                />
                <label className="input__file-button">
                  <button className="secl">Select file</button>
                </label>
              </div>
            </div>
            <div className="image__word">
              <span>Image</span>
              <div className="select__src">
                <input
                  name="file"
                  type="file"
                  id="input__file"
                  className="input__file"
                />
                <label className="input__file-button">
                  <button className="secl">Select file</button>
                </label>
              </div>
            </div>
          </form>
        </div>
        {cards.map((item, ind) => (
          <div key={ind}>
            <p>{item.name}</p>
          </div>
        ))}
      </InfiniteScroll>
    </div>
  );
};

export default CardsAdminPanel;
